import { createRouter, createWebHistory } from 'vue-router';

// 导入页面组件
import LoginPage from '../views/LoginPage.vue'; // 登录页面
import DetailsPage from '../views/DetailsPage.vue'; // 收支明细页面
import IncomePage from '../views/IncomePage.vue'; // 收入记账页面
import ExpensePage from '../views/ExpensePage.vue'; // 支出记账页面

// 路由定义
const routes = [
    { path: '/', component: LoginPage }, // 登录页面
    { path: '/analysis', component: DetailsPage }, // 收支明细页面
    { path: '/income', component: IncomePage }, // 收入记账页面
    { path: '/expense', component: ExpensePage }, // 支出记账页面
];

// 创建路由实例
const router = createRouter({
    history: createWebHistory(),
    routes,
});

export default router;
