import { createRouter, createWebHistory } from 'vue-router';

// 导入页面组件
import LoginPage from '../views/LoginPage.vue'; // 登录页面
import AnalysisPage from '../views/AnalysisPage.vue'; // 收支明细页面
import BudgetPage from '../views/BudgetPage.vue'; // 预算页面
import UploadPage from '../views/UploadPage.vue'; // 用户上传页面
import IncomeStatsPage from '../views/IncomeStatsPage.vue'; // 收入统计页面
import ExpenseStatsPage from '../views/ExpenseStatsPage.vue'; // 支出统计页面

// 路由配置
const routes = [
    { path: '/', redirect: '/login' }, // 默认跳转到登录页面
    { path: '/login', component: LoginPage }, // 登录页面
    { path: '/analysis', component: AnalysisPage }, // 收支明细页面
    { path: '/budget', component: BudgetPage }, // 预算页面
    { path: '/upload', component: UploadPage }, // 用户上传页面
    { path: '/income-stats', component: IncomeStatsPage }, // 收入统计页面
    { path: '/expense-stats', component: ExpenseStatsPage }, // 支出统计页面
    { path: '/:catchAll(.*)', redirect: '/login' }, // 捕获所有未匹配路径，重定向到登录页面
];

// 创建路由实例
const router = createRouter({
    history: createWebHistory(), // 使用 HTML5 History 模式
    routes, // 应用路由配置
});

export default router;
