import { createRouter, createWebHistory } from 'vue-router';

// 导入页面组件
import LoginPage from '../views/LoginPage.vue';
import AnalysisPage from '../views/AnalysisPage.vue';
import BudgetPage from '../views/BudgetPage.vue';
import UploadPage from '../views/UploadPage.vue';
import IncomeStatsPage from '../views/IncomeStatsPage.vue';
import ExpenseStatsPage from '../views/ExpenseStatsPage.vue';
import MyPage from '../views/MyPage.vue'; // 新增的 "我的" 页面

const routes = [
    { path: '/', redirect: '/login' },
    { path: '/login', component: LoginPage },
    { path: '/analysis', component: AnalysisPage },
    { path: '/budget', component: BudgetPage },
    { path: '/upload', component: UploadPage },
    { path: '/income-stats', component: IncomeStatsPage },
    { path: '/expense-stats', component: ExpenseStatsPage },
    { path: '/my', component: MyPage }, // 添加 "我的" 页面路由
    { path: '/:catchAll(.*)', redirect: '/login' }, // 未匹配路由重定向到登录页面
];

const router = createRouter({
    history: createWebHistory(),
    routes,
});

export default router;
